package reverse_string

import (
	"fmt"
	"strings"
	"time"
)

func Reverse(str string) string {
	strArr := strings.Split(str, "")
	size := len(strArr)
	if size < 2 {
		return str
	}

	for i := size - 1; i >= size/2; i-- {
		strArr[i], strArr[size-i-1] = strArr[size-i-1], strArr[i]
	}

	return strings.Join(strArr, "")
}

func Reverse2(str string) string {
	size := len(str)
	if size < 2 {
		return str
	}

	revStr := ""
	for i := size - 1; i >= 0; i-- {
		revStr = revStr + string(str[i])
	}

	return revStr
}

func Reverse3(str string) string {
	size := len(str)
	if size < 2 {
		return str
	}

	revStr := str
	for i := size - 1; i >= 0; i-- {
		revStr = revStr[:i] + string(str[i]) + revStr[i+1:]
	}

	return revStr
}

